import React from 'react'
import { useTranslation, withTranslation, Trans } from 'react-i18next';
import './am.css';
import '../bootstrap.min.css';
import '../App.css';

export default function Amtr() {

    return (
    
        <>
        <div className='table'>
            <div className='tr'>
                <div className='td'>
                    <button className='btn btn-primary btn-sm' onClick={ () => { go(1); }} >imagem 1</button>
                    &nbsp;
                    <button className='btn btn-primary btn-sm' onClick={ () => { go(0); }} >imagem 2</button>
                </div>
                <div className='td'>
                
                </div>
            </div>
            
        </div>
            <canvas id="mainc" className='canvas' />
        </>
    )

    // window.addEventListener('load', this.init());
    const cnv = document.getElementById('mainc');
    const ctx = cnv.getContext("2d");
    const { t, i18n } = useTranslation();
    
    const img = new Image();    
    var jimg;
	var jch;// = false;
    // 
    function init() {
        cnv.width = 150;
        cnv.height = 200;
        jch = false;
        alert('d');
    }
    // var pic = [];
    window.setTimeout('init()', 1000);
    function httpGetAsync(theUrl, callback)
    {
        var xmlHttp = new XMLHttpRequest();
        xmlHttp.onreadystatechange = function() { 
            if (xmlHttp.readyState == 4 && xmlHttp.status == 200)
                callback(xmlHttp.responseText);
        }
        xmlHttp.open("GET", theUrl, true); // true for asynchronous 
        xmlHttp.send(null);
    }


    
    

    function cb1(_arg) {
         
		// jimg = document.getElementById('img1');
		// jimg.src = _arg;
		
		// //var image = new Image();
		img.onload = function() {
			if (jch) 
				ctx.drawImage(img, 0, 0);
			jch = false;
		};
		img.src = _arg;
    }
    
    function go(_arg) {
		jch = true;
        var s;
        if (_arg == 1)
            s = 'he64.txt';
        else
            s = 'she64.txt'

		httpGetAsync(s, cb1); 
    }
    

    

    // httpGetAsync("she2dec.txt", cb1); 

    // init();
    
       


}
