  function mem() {
    var img = new Image();
    const cnv = document.getElementById('mainc');
    const ctx = cnv.getContext("2d");
    ctx.drawImage(img, 0, 0);
    const imageData = ctx.getImageData(0, 0, cnv.width, cnv.height);
    const data = imageData.data;
    // let data = [];
    // for (let i = 0; i < 400; i++) {
    //     data[i] = randomInteger(0, 1) > 0 ? 0 : 255;
    // }

    console.log(data.length);
    console.log(data);
    console.log(cnv.width + ' ' + cnv.height);
    // return;
    let vec = [];
    let c = 0;
    for (let i = 0; i < data.length; i+=4) {
        vec[c] = data[i] === 255 ? -1 : 1;
        c++;
    }
    let t = mtx1x1(vec, vec);
    console.log(t);
    if (m == null) 
        m = t;
    else 
        m = mtx1xs(t, m);
    console.log(m);
  }

  function randomInteger(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;//arr[arr.lenght - 1];
  }

  function noise() {
    var img = new Image();
    const cnv = document.getElementById('mainc');
    const ctx = cnv.getContext("2d");
    ctx.drawImage(img, 0, 0);
    const imageData = ctx.getImageData(0, 0, cnv.width, cnv.height);
    const data = imageData.data;
    let muts = parseInt(m.length * .1);
    let mtctr = 0;
    let v = [];
    let pt = 0;
    for (var i = 0; i < muts; i++) {
        while (true) {
            pt = randomInteger(0, data.length - 1);
            if (pt == 0 || ((pt + 1) % 4 == 0))
                break;
        }
        let c = data[pt] == 255 ? 0 : 255;
        data[pt] = c;
        data[pt + 1] = c;
        data[pt + 2] = c;
    }
    ctx.putImageData(imageData, 0, 0);
  }
  
  function plot() {
    var img = new Image();
    const cnv = document.getElementById('mainc');
    const ctx = cnv.getContext("2d");
    ctx.drawImage(img, 0, 0);
    const imageData = ctx.getImageData(0, 0, cnv.width, cnv.height);
    const data = imageData.data;
    let v = [];
    let c = 0;
    for (var i = 0; i < data.length; i+=4) {
        v[c] = data[i];
        c++;
    }
    let v2 = mtx1xm(v, m);
    console.log(v2);
    c = 0;
    for (var i = 0; i < data.length; i+=4) {
        data[i] = v2[c] < 0 ? 0 : 255;
        data[i + 1] = data[i];
        data[i + 2] = data[i];
        c++;
    }
    ctx.putImageData(imageData, 0, 0);
  }

  function memclear() {
    m = [];
  }

}
