const Breadcrumbs = () => {
  const breadcrumbs = useBreadcrumbs();

  return (
    <div className="breadcrumbs">
      {breadcrumbs.map(({ breadcrumb, match }, index) => (
        <div className="bc" key={match.url}>
          <Link to={match.url || ""}>{breadcrumb}</Link>
          {index < breadcrumbs.length - 1 && ">"}
        </div>
      ))}
    </div>
  );
};
