import React, { Component } from 'react';
import { useTranslation, withTranslation, Trans } from 'react-i18next';
import './App.css';
import './bootstrap.min.css';
import Main from './main/main';
import TopBar from './topbar/topbar';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from 'react-router-dom';
import TestPage from './pages/testpage/testpage';

function Page() {
  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <>
      <div className='alright'>
        <img className='flags' src={require("./img/br.png")} alt=""  onClick={() => changeLanguage('pt')} />
        &nbsp;
        <img className='flags' src={require("./img/uk.png")} alt=""  onClick={() => changeLanguage('en')} />
        &nbsp;
      </div>

      <div className="col-md-8 content main fnt1" >
        <Router>

          <div className="row">
            <Routes>
              <Route exact path='/' element={< Main />}></Route>
              <Route  path='/posts' element={< TestPage />}></Route>
            </Routes> 
          </div>

        </Router>
      </div>
  </> );
}

class App extends Component {
  render() {
    return (
      <>
      <div className="row" style={{ textAlign: 'center'}}>
    <TopBar />
    <Page />
    </div>
    </>
  );
  }
  }



export default App;
