function App() {
  return (
    <>
    <TopBar />
    <Router>

      <Routes>
          <Route exact path="/"  component={Page}>
            <Suspense fallback={<Loader />}>
              <Page />
            </Suspense>
          </Route>
          <Route path="/posts/" component={TestPage}>
              <TestPage />
          </Route> 
        </Routes>
    </Router>
    </>
  );
}

export default App;
