import React from 'react';
import './topbar.css';
import '../bootstrap.min.css';
import '../App';

export default function TopBar() {
  return (
    <>
    <div className='top'>
        <div className="row">
            <div className="col-md-4" >
                <h2>&nbsp;Pragmata</h2>
            </div>
            <div className="col-md-4" style={{ textAlign: 'center'}} >
                <img className='a1' src={require("../img/ukraine.png")}  alt="" />
                <br />
                <img src={require("../img/freeUkraine.png")} style={{width: 110}} alt="" />
            </div>
            <div className="col-md-4 alright"  >
                <h2>πράγματα</h2>
            </div>
        </div>
    </div>
    <div className='alright'>
        <img className='flags' src={require("./img/br.png")} alt=""  onClick={() => changeLanguage('pt')} />
        &nbsp;
        <img className='flags' src={require("./img/uk.png")} alt=""  onClick={() => changeLanguage('en')} />
        &nbsp;
    </div>
  </>
  )
}
