import React, { Component, Suspense } from 'react';
import { useTranslation, withTranslation, Trans } from 'react-i18next';
import './App.css';
import './bootstrap.min.css';
import Main from './main/main';
import TopBar from './topbar/topbar';

// class LegacyWelcomeClass extends Component {
//   render() {
//     const { t } = this.props;
//     return <h2>{t('title')}</h2>;
//   }
// }
// const Welcome = withTranslation()(LegacyWelcomeClass);

// page uses the hook
function Page() {
  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <div className="row" style={{ textAlign: 'center'}}>
      <div className='alright'>
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        {/* <Welcome /> */}
        <button className='flags' type="button" onClick={() => changeLanguage('pt')} >
          <img className='flags' src={require("./img/br.png")} alt="" />
        </button>
        &nbsp;
        <button className='flags' type="button" onClick={() => changeLanguage('en')}>
        <img className='flags' src={require("./img/uk.png")} alt="" />
        </button>
      </div>
      <div className="col-md-8 content main" >
        <Main />
      </div>

    </div>
  );
}

// loading component for suspense fallback
const Loader = () => (
  <div className="App">
    {/* <img src={logo} className="App-logo" alt="logo" /> */}
    <div>loading...</div>
  </div>
);

// here app catches the suspense from page in case translations are not yet loaded
// export default function App() {
//   return (
//     <Suspense fallback={<Loader />}>
//       <Page />
//     </Suspense>
//   );
// }

function App() {
  return (
    <>
    <TopBar />
    <Suspense fallback={<Loader />}>
      <Page />
    </Suspense>
    </>
  );
}

export default App;
