import React from 'react'
import { useTranslation, withTranslation, Trans } from 'react-i18next';
import './am.css';
import '../bootstrap.min.css';
import '../App.css';

export default function Amtr() {

    return (
    
        <>
        <div className='table'>
            <div className='tr'>
                <div className='td w160'  >
                <canvas id="mainc" className='canvas' />
                </div>
                <div className='td tst'>
                    <button className='btn btn-primary btn-sm' onClick={ () => { go(1); }} >imagem 1</button>
                    &nbsp;
                    <button className='btn btn-primary btn-sm' onClick={ () => { go(0); }} >imagem 2</button>
                    &nbsp;
                    <button className='btn btn-primary btn-sm' onClick={ () => { grayscale(); }} >grayscale</button>
                    &nbsp;
                    <button className='btn btn-primary btn-sm' onClick={ () => { bw(); }} >bw</button>
                    &nbsp;
                    <button className='btn btn-primary btn-sm' onClick={ () => { mem(); }} >bw</button>

                </div>
                
            </div>
            
        </div>
            
        </>
    )

    // window.addEventListener('load', this.init());
    const cnv = document.getElementById('mainc');
    const ctx = cnv.getContext("2d");
    const { t, i18n } = useTranslation();
    
    const img = new Image();    
    var jimg;
    var m;
	var jch;// = false;
    // 
    // function init() {
    //     cnv.width = 150;
    //     cnv.height = 200;
    //     jch = false;
    //     // alert('d');
    // }
    // var pic = [];
    // window.setTimeout('init()', 1000);
    function httpGetAsync(theUrl, callback)
    {
        var xmlHttp = new XMLHttpRequest();
        xmlHttp.onreadystatechange = function() { 
            if (xmlHttp.readyState == 4 && xmlHttp.status == 200)
                callback(xmlHttp.responseText);
        }
        xmlHttp.open("GET", theUrl, true); // true for asynchronous 
        xmlHttp.send(null);
    }


    function cb1(_arg) {
        const cnv = document.getElementById('mainc');
        const ctx = cnv.getContext("2d");
        cnv.width = 150;
        cnv.height = 200;
		var image = new Image();
		image.onload = function() {
			if (jch) 
				ctx.drawImage(image, 0, 0);
			jch = false;
		};
		image.src = _arg;
    }
    
    function go(_arg) {
		jch = true;
        var s;
        if (_arg == 1)
            s = '../he64.txt';
        else
            s = '../she64.txt'
		httpGetAsync(s, cb1); 
    }
    
    function bw()  {
        const cnv = document.getElementById('mainc');
        const ctx = cnv.getContext("2d");
        var img = new Image();
		var thr = 150;
		ctx.drawImage(img, 0, 0);
		const imageData = ctx.getImageData(0, 0, cnv.width, cnv.height);
		const data = imageData.data;
		for (let i = 0; i < data.length; i += 4) {
			const avg = data[i];
			if (avg < thr) {
				data[i] = 0; // red
				data[i + 1] = 0; // green
				data[i + 2] = 0; // blue
			}
			else {
				data[i] = 255; // red
				data[i + 1] = 255; // green
				data[i + 2] = 255; // blue
			}
			
		}
		ctx.putImageData(imageData, 0, 0);
	}
    
    function grayscale()  {
        var img = new Image();
        const cnv = document.getElementById('mainc');
        const ctx = cnv.getContext("2d");
		ctx.drawImage(img, 0, 0);
		const imageData = ctx.getImageData(0, 0, cnv.width, cnv.height);
		const data = imageData.data;
		for (let i = 0; i < data.length; i += 4) {
			const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
			data[i] = avg; // red
			data[i + 1] = avg; // green
			data[i + 2] = avg; // blue
			//console.log(avg)
		}
		ctx.putImageData(imageData, 0, 0);
	}
    
    
function mtx1xm(v1, m1) {
    var res = [];
    for (var i = 0; i < v1.length; i++) {
        res[i] = 0;
        for (var j = 0; j < m1[i].length; j++) {
            res[i] += v1[j] * m1[j][i];
        }
    }
    
    return res;
  }
  
  function mtx1xs(m1, m2) {
    var res = [];
    for (var i = 0; i < m1.length; i++) {
        res[i] = [];
        for (var j = 0; j < m1[i].length; j++) {
            res[i][j] = m1[i][j] + m2[i][j];
        }
    }
    // console.log(res);
    return res;
  }
  
  function mtx1x1(m1, m2) {
    var result = [];
    for (var i = 0; i < m1.length; i++) {
        result[i] = [];
        var sum = 0;
        for (var j = 0; j < m2.length; j++) {
            sum = m1[i] * m2[j];
            result[i][j] = sum;
            // console.log(sum);
        }
    }
    return result;
  }
  
