    const { t, i18n } = useTranslation();
  return (
    <>
    <div className='top'>
        <div className="row">
            <div className="col-md-4" >
                <h2>&nbsp;Pragmata</h2>
            </div>
            <div className="col-md-4" style={{ textAlign: 'center'}} >
                <img className='a1' src={require("../img/ukraine.png")} title={t('ukr')} alt="" />
