                <img src={require("../img/freeUkraine.png")} title={t('ukr')} style={{width: 110}} alt="" />
            </div>
            <div className="col-md-4 alright"  >
                <h2>πράγματα</h2>
            </div>
        </div>
    </div>
    
    
  </>
  );
}



