import React from 'react';
import './am.css';
import './pixel.js';
import '../bootstrap.min.css';
import '../App.css';

export default function Am() {
    
    const cnv = document.getElementById('mainc');
    cnv.width = 150;
    cnv.height = 180;
    const ctx = cnv.getContext("2d");
    const p = new Pixel(0,0,1);
    // p.draw(ctx);
    return (
    <div className="row">
        <div className="col-md-6">
            <canvas id="mainc" className='canvas' />
        </div>
        <div className="col-md-6">llkj</div>
        
    </div>
  )
}

class Pixel {
    constructor(x, y, val) {
        this.x = x;
        this.y = y;
        this.width = 30;
        this.height = 30;
        this.val = val;

       
    }

   

    draw(ctx) {
        ctx.beginPath();
        ctx.rect(
            -this.width/2,
            -this.height/2,
            this.width,
            this.height
        );
        ctx.fill();
    }
}
